<?php function check_edit($data,$type="cabang|periode|dihapus|disetujui",$fields = ""){if(empty($fields["cabang"]))$fields["cabang"]=$_SESSION["setting"]["field_cabang"];if(empty($fields["tanggal"]))$fields["tanggal"]=$_SESSION["setting"]["field_tanggal"];if(empty($fields["status_aktif"]))$fields["status_aktif"]=$_SESSION["setting"]["field_status_aktif"];if(empty($fields["status_disetujui"]))$fields["status_disetujui"]=$_SESSION["setting"]["field_status_disetujui"];if(strstr($type,"cabang")&&$data[$fields["cabang"]]!=$_SESSION["cabang"]["nomor"])set_alert(get_message(717));if(strstr($type,"periode")&&!check_periode($data[$fields["tanggal"]]))set_alert(get_message(718));if(strstr($type,"dihapus")&&$data[$fields["status_aktif"]]==0)set_alert(get_message(206));if(strstr($type,"disetujui")&&$data[$fields["status_disetujui"]]>0)set_alert(get_message(719));return true;} ?>
